import { Test, TestingModule } from '@nestjs/testing';
import { AppModule } from '../../app.module';
import { UserType } from 'fundscraper-model-enums';
import { UserIdentification } from '../../security/UserIdentification';
import { MailDal } from '../../dal/Mail.provider';
import { Mail } from '../../model/Mail.entity.generated';
import { EMailStatus } from 'fundscraper-model-enums';
import { AllUserDal } from '../../dal/AllUser.provider';

describe('MailDal-generated', () => {
    let dal: MailDal;
    let moduleFixture: TestingModule;

    beforeEach(async () => {
        moduleFixture = await Test.createTestingModule({
            imports: [AppModule],
        }).compile();

        dal = await moduleFixture.resolve<MailDal>(MailDal);
        dal.setUserForRequest(new UserIdentification(2, UserType.admin_user));
    });

    it('Find First 2', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 2);
        expect(workingResults.length).toBeGreaterThanOrEqual(0);
        expect(workingResults.length).toBeLessThanOrEqual(2);
    });

    it('Find one by id', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let oneResult = await dal.findOneById(workingResults[0].id);
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Find one by filtered id', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, null, null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    // now expand per property
    it('Find one and from to all_users', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, ['from'], null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Find one and last_changed_by to all_users', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, ['last_changed_by'], null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Find one and to to all_users', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, ['to'], null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Create one then update', async () => {
        let newObject = new Mail();

        // assign value to each property
        // newObject.id = ?; // number
        // newObject.to_id = ?; // number
        // newObject.from_id = ?; // number
        newObject.address_sent_to = ''; // string 
        newObject.subject = ''; // string 
        newObject.body = ''; // string 
        newObject.raw_body = ''; // string 
        newObject.sent_ok = true; // boolean boolean
        newObject.attachment = 'abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz'; // string 
        newObject.flag = true; // boolean boolean
        newObject.stared = true; // boolean boolean
        newObject.status = 1 as unknown as EMailStatus // enum; // EMailStatus EMailStatus
        newObject.is_deleted = true; // boolean boolean
        // newObject.last_changed_by_id = ?; // number
        // newObject.createtime = ?; // Date
        // newObject.updatetime = ?; // Date
        let dalAllUser = await moduleFixture.resolve<AllUserDal>(AllUserDal);
        dalAllUser.setUserForRequest(new UserIdentification(2, UserType.admin_user));
        let fkfrom = await dalAllUser.findAllByFilter('', undefined, undefined, undefined, 1, 1); // AllUser 
        if (fkfrom.length > 0)
            newObject.from = fkfrom[0] as any; // AllUser 
        newObject.from_id = (fkfrom[0] as any).id;
        let fklast_changed_by = await dalAllUser.findAllByFilter('', undefined, undefined, undefined, 1, 1); // AllUser 
        if (fklast_changed_by.length > 0)
            newObject.last_changed_by = fklast_changed_by[0] as any; // AllUser 
        newObject.last_changed_by_id = (fklast_changed_by[0] as any).id;
        let fkto = await dalAllUser.findAllByFilter('', undefined, undefined, undefined, 1, 1); // AllUser 
        if (fkto.length > 0)
            newObject.to = fkto[0] as any; // AllUser 
        newObject.to_id = (fkto[0] as any).id;

        let newObjectId = await dal.create(newObject);
        let savedObject = await dal.findOneById(newObjectId);

        // compare the two objects
        expect(savedObject.address_sent_to).toBe(newObject.address_sent_to);
        expect(savedObject.subject).toBe(newObject.subject);
        expect(savedObject.body).toBe(newObject.body);
        expect(savedObject.raw_body).toBe(newObject.raw_body);
        expect(savedObject.sent_ok).toBe(newObject.sent_ok);
        expect(savedObject.attachment).toBe(newObject.attachment);
        expect(savedObject.flag).toBe(newObject.flag);
        expect(savedObject.stared).toBe(newObject.stared);
        expect((typeof savedObject.status == 'string') ? parseInt(savedObject.status) : savedObject.status).toBe(newObject.status);
        expect(savedObject.is_deleted).toBe(newObject.is_deleted);

        // update save and compare
        newObject.id = newObjectId as number;
        newObject.address_sent_to = ''; // string
        newObject.subject = ''; // string
        newObject.body = ''; // string
        newObject.raw_body = ''; // string
        newObject.sent_ok = false; // boolean
        newObject.attachment = '01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmno'; // string
        newObject.flag = false; // boolean
        newObject.stared = false; // boolean
        newObject.status = 2 as unknown as EMailStatus // enum; // EMailStatus
        newObject.is_deleted = false; // boolean

        await dal.save(newObject);
        let reloadedObject = await dal.findOneById(newObjectId);

        // compare the two objects
        expect(reloadedObject.address_sent_to).toBe(newObject.address_sent_to);
        expect(reloadedObject.subject).toBe(newObject.subject);
        expect(reloadedObject.body).toBe(newObject.body);
        expect(reloadedObject.raw_body).toBe(newObject.raw_body);
        expect(reloadedObject.sent_ok).toBe(newObject.sent_ok);
        expect(reloadedObject.attachment).toBe(newObject.attachment);
        expect(reloadedObject.flag).toBe(newObject.flag);
        expect(reloadedObject.stared).toBe(newObject.stared);
        expect((typeof reloadedObject.status == 'string') ? parseInt(reloadedObject.status) : reloadedObject.status).toBe(newObject.status);
        expect(reloadedObject.is_deleted).toBe(newObject.is_deleted);

        // delete and check that its gone
        await dal.removeOneById(newObjectId);
        let objectAfterDelete = await dal.findOneById(newObjectId);
        expect(objectAfterDelete).toBeNull();
    });
});
