import { Test, TestingModule } from '@nestjs/testing';
import { AppModule } from '../../app.module';
import { UserType } from 'fundscraper-model-enums';
import { UserIdentification } from '../../security/UserIdentification';
import { ProjectAccountDal } from '../../dal/ProjectAccount.provider';
import { ProjectAccount } from '../../model/ProjectAccount.entity.generated';
import { PaymentMethodType } from 'fundscraper-model-enums';
import { AllUserDal } from '../../dal/AllUser.provider';
import { ProjectDal } from '../../dal/Project.provider';

describe('ProjectAccountDal-generated', () => {
    let dal: ProjectAccountDal;
    let moduleFixture: TestingModule;

    beforeEach(async () => {
        moduleFixture = await Test.createTestingModule({
            imports: [AppModule],
        }).compile();

        dal = await moduleFixture.resolve<ProjectAccountDal>(ProjectAccountDal);
        dal.setUserForRequest(new UserIdentification(2, UserType.admin_user));
    });

    it('Find First 2', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 2);
        expect(workingResults.length).toBeGreaterThanOrEqual(0);
        expect(workingResults.length).toBeLessThanOrEqual(2);
    });

    it('Find one by id', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let oneResult = await dal.findOneById(workingResults[0].id);
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Find one by filtered id', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, null, null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    // now expand per property
    it('Find one and last_changed_by to all_users', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, ['last_changed_by'], null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Find one and project to projects', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, ['project'], null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Create one then update', async () => {
        let newObject = new ProjectAccount();

        // assign value to each property
        // newObject.id = ?; // number
        // newObject.project_id = ?; // number
        newObject.method = -1; // number 
        newObject.eligibility_types = 'abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz'; // string 
        newObject.payment_type = 1 as unknown as PaymentMethodType // enum; // PaymentMethodType PaymentMethodType
        newObject.trust_name = ''; // string 
        newObject.trust_address = ''; // string 
        newObject.trust_contact = 'abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklm'; // string 
        newObject.institution = ''; // string 
        newObject.institution_number = 'abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz'; // string 
        newObject.branch_number = 'abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz'; // string 
        newObject.bank_account_number = 'abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz'; // string 
        newObject.branch_mailing_address = ''; // string 
        newObject.swift_code = 'abcdefghijklmnopqrstuvwxy'; // string 
        newObject.sort_order = -1; // number 
        newObject.custom_text = ''; // string 
        newObject.is_active = true; // boolean boolean
        newObject.is_deleted = true; // boolean boolean
        // newObject.last_changed_by_id = ?; // number
        // newObject.createtime = ?; // Date
        // newObject.updatetime = ?; // Date
        let dalAllUser = await moduleFixture.resolve<AllUserDal>(AllUserDal);
        dalAllUser.setUserForRequest(new UserIdentification(2, UserType.admin_user));
        let fklast_changed_by = await dalAllUser.findAllByFilter('', undefined, undefined, undefined, 1, 1); // AllUser 
        if (fklast_changed_by.length > 0)
            newObject.last_changed_by = fklast_changed_by[0] as any; // AllUser 
        newObject.last_changed_by_id = (fklast_changed_by[0] as any).id;
        let dalProject = await moduleFixture.resolve<ProjectDal>(ProjectDal);
        dalProject.setUserForRequest(new UserIdentification(2, UserType.admin_user));
        let fkproject = await dalProject.findAllByFilter('', undefined, undefined, undefined, 1, 1); // Project 
        if (fkproject.length > 0)
            newObject.project = fkproject[0] as any; // Project 
        newObject.project_id = (fkproject[0] as any).id;

        let newObjectId = await dal.create(newObject);
        let savedObject = await dal.findOneById(newObjectId);

        // compare the two objects
        expect(savedObject.method).toBe(newObject.method);
        expect(savedObject.eligibility_types).toBe(newObject.eligibility_types);
        expect((typeof savedObject.payment_type == 'string') ? parseInt(savedObject.payment_type) : savedObject.payment_type).toBe(newObject.payment_type);
        expect(savedObject.trust_name).toBe(newObject.trust_name);
        expect(savedObject.trust_address).toBe(newObject.trust_address);
        expect(savedObject.trust_contact).toBe(newObject.trust_contact);
        expect(savedObject.institution).toBe(newObject.institution);
        expect(savedObject.institution_number).toBe(newObject.institution_number);
        expect(savedObject.branch_number).toBe(newObject.branch_number);
        expect(savedObject.bank_account_number).toBe(newObject.bank_account_number);
        expect(savedObject.branch_mailing_address).toBe(newObject.branch_mailing_address);
        expect(savedObject.swift_code).toBe(newObject.swift_code);
        expect(savedObject.sort_order).toBe(newObject.sort_order);
        expect(savedObject.custom_text).toBe(newObject.custom_text);
        expect(savedObject.is_active).toBe(newObject.is_active);
        expect(savedObject.is_deleted).toBe(newObject.is_deleted);

        // update save and compare
        newObject.id = newObjectId as number;
        newObject.method = 1; // number
        newObject.eligibility_types = '01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmno'; // string
        newObject.payment_type = 2 as unknown as PaymentMethodType // enum; // PaymentMethodType
        newObject.trust_name = ''; // string
        newObject.trust_address = ''; // string
        newObject.trust_contact = '01234567890abcdefghijklmnopqrstuvwxyz01234567890ab'; // string
        newObject.institution = ''; // string
        newObject.institution_number = '01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmno'; // string
        newObject.branch_number = '01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmno'; // string
        newObject.bank_account_number = '01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmnopqrstuvwxyz01234567890abcdefghijklmno'; // string
        newObject.branch_mailing_address = ''; // string
        newObject.swift_code = '01234567890abcdefghijklmn'; // string
        newObject.sort_order = 1; // number
        newObject.custom_text = ''; // string
        newObject.is_active = false; // boolean
        newObject.is_deleted = false; // boolean

        await dal.save(newObject);
        let reloadedObject = await dal.findOneById(newObjectId);

        // compare the two objects
        expect(reloadedObject.method).toBe(newObject.method);
        expect(reloadedObject.eligibility_types).toBe(newObject.eligibility_types);
        expect((typeof reloadedObject.payment_type == 'string') ? parseInt(reloadedObject.payment_type) : reloadedObject.payment_type).toBe(newObject.payment_type);
        expect(reloadedObject.trust_name).toBe(newObject.trust_name);
        expect(reloadedObject.trust_address).toBe(newObject.trust_address);
        expect(reloadedObject.trust_contact).toBe(newObject.trust_contact);
        expect(reloadedObject.institution).toBe(newObject.institution);
        expect(reloadedObject.institution_number).toBe(newObject.institution_number);
        expect(reloadedObject.branch_number).toBe(newObject.branch_number);
        expect(reloadedObject.bank_account_number).toBe(newObject.bank_account_number);
        expect(reloadedObject.branch_mailing_address).toBe(newObject.branch_mailing_address);
        expect(reloadedObject.swift_code).toBe(newObject.swift_code);
        expect(reloadedObject.sort_order).toBe(newObject.sort_order);
        expect(reloadedObject.custom_text).toBe(newObject.custom_text);
        expect(reloadedObject.is_active).toBe(newObject.is_active);
        expect(reloadedObject.is_deleted).toBe(newObject.is_deleted);

        // delete and check that its gone
        await dal.removeOneById(newObjectId);
        let objectAfterDelete = await dal.findOneById(newObjectId);
        expect(objectAfterDelete).toBeNull();
    });
});
