import { Test, TestingModule } from '@nestjs/testing';
import { AppModule } from '../../app.module';
import { UserType } from 'fundscraper-model-enums';
import { UserIdentification } from '../../security/UserIdentification';
import { UserAnswerDal } from '../../dal/UserAnswer.provider';
import { UserAnswer } from '../../model/UserAnswer.entity.generated';
import { RegistrationQuestionCategory } from 'fundscraper-model-enums';
import { QuestionAnswerType } from 'fundscraper-model-enums';
import { UserEntityDal } from '../../dal/UserEntity.provider';
import { AllUserDal } from '../../dal/AllUser.provider';
import { RegistrationQuestionDal } from '../../dal/RegistrationQuestion.provider';

describe('UserAnswerDal-generated', () => {
    let dal: UserAnswerDal;
    let moduleFixture: TestingModule;

    beforeEach(async () => {
        moduleFixture = await Test.createTestingModule({
            imports: [AppModule],
        }).compile();

        dal = await moduleFixture.resolve<UserAnswerDal>(UserAnswerDal);
        dal.setUserForRequest(new UserIdentification(2, UserType.admin_user));
    });

    it('Find First 2', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 2);
        expect(workingResults.length).toBeGreaterThanOrEqual(0);
        expect(workingResults.length).toBeLessThanOrEqual(2);
    });

    it('Find one by id', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let oneResult = await dal.findOneById(workingResults[0].id);
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Find one by filtered id', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, null, null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    // now expand per property
    it('Find one and entity to users_entity', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, ['entity'], null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Find one and last_changed_by to all_users', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, ['last_changed_by'], null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Find one and question to registration_questionary', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, ['question'], null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Find one and user to all_users', async () => {
        let workingResults = await dal.findAllByFilter(null, null, null, null, 1, 10);
        let results = await dal.findAllByFilter(`id = ${workingResults[0].id}`, null, ['user'], null, 1, 10);
        expect(results.length).toBe(1);
        let oneResult = results[0];
        expect(oneResult.id).toBe(workingResults[0].id);
    });

    it('Create one then update', async () => {
        let newObject = new UserAnswer();

        // assign value to each property
        // newObject.id = ?; // number
        // newObject.user_id = ?; // number
        // newObject.entity_id = ?; // number
        newObject.category = 1 as unknown as RegistrationQuestionCategory // enum; // RegistrationQuestionCategory RegistrationQuestionCategory
        // newObject.question_id = ?; // number
        newObject.question_text = ''; // string 
        newObject.answer_type = 1 as unknown as QuestionAnswerType // enum; // QuestionAnswerType QuestionAnswerType
        newObject.answer_text = ''; // string 
        newObject.is_confirmed = true; // boolean boolean
        newObject.notes = ''; // string 
        newObject.status = -1; // number 
        newObject.is_deleted = true; // boolean boolean
        // newObject.last_changed_by_id = ?; // number
        // newObject.createtime = ?; // Date
        // newObject.updatetime = ?; // Date
        let dalUserEntity = await moduleFixture.resolve<UserEntityDal>(UserEntityDal);
        dalUserEntity.setUserForRequest(new UserIdentification(2, UserType.admin_user));
        let fkentity = await dalUserEntity.findAllByFilter('', undefined, undefined, undefined, 1, 1); // UserEntity 
        if (fkentity.length > 0)
            newObject.entity = fkentity[0] as any; // UserEntity 
        newObject.entity_id = (fkentity[0] as any).id;
        let dalAllUser = await moduleFixture.resolve<AllUserDal>(AllUserDal);
        dalAllUser.setUserForRequest(new UserIdentification(2, UserType.admin_user));
        let fklast_changed_by = await dalAllUser.findAllByFilter('', undefined, undefined, undefined, 1, 1); // AllUser 
        if (fklast_changed_by.length > 0)
            newObject.last_changed_by = fklast_changed_by[0] as any; // AllUser 
        newObject.last_changed_by_id = (fklast_changed_by[0] as any).id;
        let dalRegistrationQuestion = await moduleFixture.resolve<RegistrationQuestionDal>(RegistrationQuestionDal);
        dalRegistrationQuestion.setUserForRequest(new UserIdentification(2, UserType.admin_user));
        let fkquestion = await dalRegistrationQuestion.findAllByFilter('', undefined, undefined, undefined, 1, 1); // RegistrationQuestion 
        if (fkquestion.length > 0)
            newObject.question = fkquestion[0] as any; // RegistrationQuestion 
        newObject.question_id = (fkquestion[0] as any).id;
        let fkuser = await dalAllUser.findAllByFilter('', undefined, undefined, undefined, 1, 1); // AllUser 
        if (fkuser.length > 0)
            newObject.user = fkuser[0] as any; // AllUser 
        newObject.user_id = (fkuser[0] as any).id;

        let newObjectId = await dal.create(newObject);
        let savedObject = await dal.findOneById(newObjectId);

        // compare the two objects
        expect((typeof savedObject.category == 'string') ? parseInt(savedObject.category) : savedObject.category).toBe(newObject.category);
        expect(savedObject.question_text).toBe(newObject.question_text);
        expect((typeof savedObject.answer_type == 'string') ? parseInt(savedObject.answer_type) : savedObject.answer_type).toBe(newObject.answer_type);
        expect(savedObject.answer_text).toBe(newObject.answer_text);
        expect(savedObject.is_confirmed).toBe(newObject.is_confirmed);
        expect(savedObject.notes).toBe(newObject.notes);
        expect(savedObject.status).toBe(newObject.status);
        expect(savedObject.is_deleted).toBe(newObject.is_deleted);

        // update save and compare
        newObject.id = newObjectId as number;
        newObject.category = 2 as unknown as RegistrationQuestionCategory // enum; // RegistrationQuestionCategory
        newObject.question_text = ''; // string
        newObject.answer_type = 2 as unknown as QuestionAnswerType // enum; // QuestionAnswerType
        newObject.answer_text = ''; // string
        newObject.is_confirmed = false; // boolean
        newObject.notes = ''; // string
        newObject.status = 1; // number
        newObject.is_deleted = false; // boolean

        await dal.save(newObject);
        let reloadedObject = await dal.findOneById(newObjectId);

        // compare the two objects
        expect((typeof reloadedObject.category == 'string') ? parseInt(reloadedObject.category) : reloadedObject.category).toBe(newObject.category);
        expect(reloadedObject.question_text).toBe(newObject.question_text);
        expect((typeof reloadedObject.answer_type == 'string') ? parseInt(reloadedObject.answer_type) : reloadedObject.answer_type).toBe(newObject.answer_type);
        expect(reloadedObject.answer_text).toBe(newObject.answer_text);
        expect(reloadedObject.is_confirmed).toBe(newObject.is_confirmed);
        expect(reloadedObject.notes).toBe(newObject.notes);
        expect(reloadedObject.status).toBe(newObject.status);
        expect(reloadedObject.is_deleted).toBe(newObject.is_deleted);

        // delete and check that its gone
        await dal.removeOneById(newObjectId);
        let objectAfterDelete = await dal.findOneById(newObjectId);
        expect(objectAfterDelete).toBeNull();
    });
});
